mapper.select:
SELECT 

FROM ams_flight_dynamic_pair_h a

WHERE a.id = #{id}

SELECT 

FROM ams_flight_dynamic_pair_h a


	a.del_flag = #{DEL_FLAG_NORMAL}

	${sqlMap.dsfn}

		AND TO_CHAR(a.plan_date,'YYYYMMDD') LIKE '%'||TO_CHAR(#{planDate},'YYYYMMDD')||'%'


		AND (UPPER(a.flight_num) like UPPER('%'||#{flightNum}||'%') OR UPPER(a.flight_num2) like UPPER('%'||#{flightNum}||'%'))




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT


FROM ams_flight_dynamic_pair_h a

WHERE 1=1

${sqlMap.dsfn}

	AND (a.inout_type_code = #{inoutTypeCode} or a.inout_type_code2 = #{inoutTypeCode})


	AND a.flight_company_code = #{flightCompanyCode}


	AND (a.flight_num = #{flightNum} or a.flight_num2 = #{flightNum})


	AND a.aircraft_num = #{aircraftNum}


	AND a.plan_date >= to_date(#{planDate_begin} ||' 00:00:00','yyyy-mm-dd hh24:mi:ss')


	AND a.plan_date <= to_date(#{planDate_end} ||' 23:59:59','yyyy-mm-dd hh24:mi:ss')

SELECT 

FROM ams_flight_dynamic_pair_h a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ams_flight_dynamic_pair_h SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
